import useGet from "./server_fetch/useGet";
import { Link } from "react-router-dom";

const DisplayBlog = () => {
  const { data, error, isPending } = useGet(
    "http://localhost:8000/get_all_blogs"
  );
  if (data) {
    var blogs = data;
    console.log(blogs);
    console.log(blogs.allData[1].b_author);
    return <div class="main-page-blogs">Done</div>;
  }
  if (isPending) {
    return (
      <div class="main-page-blogs">
        <button disabled>
          <div className="lds-spinner">
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
          </div>
        </button>
      </div>
    );
  }
  if (error) {
    return <div class="main-page-blogs">{error}</div>;
  } else {
    return <div class="main-page-blogs">Sorry Try Again Later</div>;
  }
};

export default DisplayBlog;
