import { Link } from "react-router-dom";

const FeedBack = (props) => {
  const [isPending, setIsPending] = useState(false);
  const [errmsg, setErrmsg] = useState(null);

  const history = useHistory();

  const Sumbit = (e) => {
    setIsPending(true);
    e.preventDefault();
    const data = { username, name, mail, password };
    fetch(props.LocationPost, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    })
      .then((res) => {
        setErrmsg(null);
        return res.json().then((err) => {
          if (err.message === "CleanRun") {
            setIsPending(false);
            history.push("/login");
          } else {
            setIsPending(false);
            setErrmsg(err.message);
          }
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="create">
      <h2>Feedback Form</h2>
      <form action={props.LocationPost} method="post">
        <label>Name:</label>
        <input type="text" name="name" required />
        <label>Mail-Id :</label>
        <input type="mail" name="mail" required />
        <label>Feedback :</label>
        <textarea type="text" name="feedback" required></textarea>
        <button>Sumbit</button>
      </form>
      <div className="form-below">
        <Link to="/feedback">feedback</Link>
        <Link to="/forgot_pass">forgot password?</Link>
      </div>
    </div>
  );
};

export default FeedBack;
