import { Link } from "react-router-dom";

const ResetPass = (props) => {
  const [name, setName] = useState("");
  const [mail_id, setMail] = useState("");
  const Sumbit = (e) => {
    setIsPending(true);
    e.preventDefault();
    const data = { name, mail_id, feedback };
    fetch(props.LocationPost, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    })
      .then((res) => {
        setErrmsg(null);
        return res.json().then((err) => {
          if (err.message === "CleanRun") {
            setIsPending(false);
            history.push("/");
          } else {
            setIsPending(false);
            setErrmsg(err.message);
          }
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="form-center">
      <h2>Reset Password</h2>
      <form action={props.LocationPost} method="post">
        <label>Username :</label>
        <input type="text" name="username" autoComplete="off" value={name}
          onChange={(e) => setName(e.target.value)} required />
        <label>Mail Id :</label>
        <input type="email" name="mail_id" autoComplete="off" value={name}
          onChange={(e) => setName(e.target.value)} required />

        <button onClick={Sumbit}>Sumbit</button>
      </form>
      <div className="form-below">
        <Link to="/feedback">feedback</Link>
        <Link to="/forgot_pass">forgot password?</Link>
      </div>
    </div>
  );
};

export default ResetPass;
