import { useState } from "react";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

const Authentication = (props) => {
  const [username, setUsername] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [mail, setMail] = useState("");
  const [feedback, setFeedback] = useState("");

  const history = useHistory();
  const Post_Fetch = (e) => {};
  //Using Conditional Rendering
  return (
    <div className="create">
      console.log({(username, password, name, mail, feedback)})
      <h2>{props.type}</h2>
      <form onSubmit={Post_Fetch}>
        {props.type !== "Feedback" && <label>Username:</label>}
        {props.type !== "Feedback" && (
          <input
            type="text"
            name="username"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        )}

        {props.type !== "Reset Password" && props.type !== "Login" && (
          <label>Name:</label>
        )}
        {props.type !== "Reset Password" && props.type !== "Login" && (
          <input
            type="text"
            name="name"
            required
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        )}

        {props.type !== "Login" && <label>Mail-Id:</label>}
        {props.type !== "Login" && (
          <input
            type="mail"
            name="mail"
            required
            value={mail}
            onChange={(e) => setMail(e.target.value)}
          />
        )}

        {props.type !== "Reset Password" && props.type !== "Feedback" && (
          <label>Password:</label>
        )}
        {props.type !== "Reset Password" && props.type !== "Feedback" && (
          <input
            type="password"
            name="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        )}

        {props.type === "Feedback" && <label>Feedback:</label>}
        {props.type === "Feedback" && (
          <textarea
            type="text"
            name="feedback"
            required
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
          ></textarea>
        )}

        {props.type !== "Login" && <button>Sumbit</button>}
        {props.type === "Login" && <button>Login</button>}
      </form>
      <div className="form-below">
        {props.type !== "Feedback" && <Link to="/feedback">feedback</Link>}
        {props.type === "Login" && (
          <Link to="/forgot_pass">forgot password?</Link>
        )}
      </div>
    </div>
  );
};

export default Authentication;
