import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { useState } from "react";
import Cookies from "js-cookie";
const jwt = require("jsonwebtoken");

const DNavbar = () => {
  const [cookies, setCookies] = useState(null);

  var cookie_get = Cookies.get("jwt");
  const history = useHistory();
  if (cookie_get) {
    const cookie_parse = jwt.verify(
      cookie_get,
      "%$iwudibdiiwd@#$wdjdwnomdw(*&whdwhd#$>idnw(*&^"
    );
    console.log(cookie_parse);
    const cookie_data = { id: cookie_parse.id, name: cookie_parse.name };
    setCookies(cookie_data);
  } else {
    history.push("/");
  }
  return (
    <nav className="navbar">
      <div className="heading">
        <h1>
          <Link to="/dashboard">Mini Blog</Link>
        </h1>
      </div>
      <div className="nav-links">
        <div className="welcome-note">
          <img
            src="/favicon.ico"
            width={30}
            height={30}
            alt={"Brand_Logo"}
          ></img>
          <strong>
            <p style={{ textAlign: "right", color: "#ff6600", margin: 10 }}>
              Welcome, {cookies}
            </p>
          </strong>
        </div>
        <Link to="/my-blog">My Blog</Link>
        <Link to="/add-blog">Add Blog</Link>
        <Link to="/blogs">Blogs</Link>
        <Link to="/logout">Logout</Link>
      </div>
    </nav>
  );
};

export default DNavbar;
