import { Redirect } from "react-router";
import { useParams } from "react-router-dom";
import useFetch from "./server_fetch/useGet";

const DeleteBlog = () => {
  const { id } = useParams();
  const { data, error, isPending } = useFetch(
    "http://localhost:8000/blog-delete/" + id
  );

  if (data) {
    if (data.message === "CleanRun") {
      return <Redirect to="/my-blog" />;
    }
    {
      isPending && (
        <button disabled>
          <div className="lds-spinner">
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
          </div>
        </button>
      );
    }
  }
};

export default DeleteBlog;
