import { Link } from "react-router-dom";

const Authentication = (props) => {
  //Using Conditional Rendering
  return (
    <div className="create">
      <h2>{props.type}</h2>
      <form action={props.HrefLocation} method="post">
        {props.type !== "Feedback" && <label>Username:</label>}
        {props.type !== "Feedback" && (
          <input type="text" name="username" required />
        )}
        {props.type !== "Reset Password" && props.type !== "Login" && (
          <label>Name:</label>
        )}
        {props.type !== "Login" && <input type="text" name="name" required />}
        {props.type !== "Login" && <label>Mail-Id:</label>}
        {props.type !== "Login" && <input type="mail" name="mail" required />}
        {props.type !== "Feedback" && <label>Password:</label>}
        {props.type !== "Feedback" && (
          <input type="password" name="password" required />
        )}
        {props.type === "Feedback" && <label>Feedback:</label>}
        {props.type === "Feedback" && (
          <textarea type="text" name="feedback" required></textarea>
        )}
        {props.type !== "Login" && <button>Sumbit</button>}
        {props.type === "Login" && <button>Login</button>}
      </form>
      <div className="form-below">
        {props.type !== "Feedback" && <Link to="/feedback">feedback</Link>}
        {props.type === "Login" && (
          <Link to="/forgot_pass">forgot password?</Link>
        )}
      </div>
    </div>
  );
};

export default Authentication;
