import { Link } from "react-router-dom";
import { useState } from "react";
import usePost from "../server_fetch/usePost";

const SignUp = (props) => {
  const [username, setUsername] = useState("");
  const [data, setData] = useState({});
  const Sumbit = () => {
    const { error } = usePost(props.LocationPost, data);
  };
  return (
    <div className="create">
      <h2>Signup</h2>
      <form action={props.LocationPost} method="post">
        <label>Username :</label>
        <input type="text" name="username" required />
        <label>Name:</label>
        <input type="text" name="name" required />
        <label>Email-Id :</label>
        <input type="mail" name="mail" required />
        <label>Password:</label>
        <input type="password" name="password" required />
        <button onClick={Sumbit}>Sumbit</button>
      </form>
      <div className="form-below">
        <Link to="/feedback">feedback</Link>
        <Link to="/forgot_pass">forgot password?</Link>
      </div>
    </div>
  );
};

export default SignUp;
